function NewsCategory(){}NewsCategory.prototype.add_category=function(){$(".box-header .btn").click(function(){xfzalert.alertOneInput({title:"添加新闻分类",placeholder:"请输入新闻分类",confirmCallback:function(t){xfzajax.post({url:"/cms/add_news_gategory/",data:{name:t},success:function(t){200==t.code?(xfzalert.alertSuccessToast("已经添加成功!"),window.location.reload()):xfzalert.alertErrorToast("您输入的分类已经存在!")}})}})})},NewsCategory.prototype.delete_category=function(){$(".table .btn-danger").click(function(){var t=$(this).parent().parent();console.log(t);var e=t.attr("data-pk"),a=t.attr("data-name");xfzalert.alertConfirm({title:"您确定要删除吗?",confirmCallback:function(){xfzajax.post({url:"/cms/delete_news_gategory/",data:{pk:e,name:a},success:function(t){200===t.code?window.location.reload():xfzalert.alertErrorToast(t.message)}})}})})},NewsCategory.prototype.edit_category=function(){$(".table .btn-warning").click(function(){var t=$(this).parent().parent(),e=t.attr("data-pk"),a=t.attr("data-name");xfzalert.alertOneInput({title:"请修改分类",value:a,confirmCallback:function(t){xfzajax.post({url:"/cms/edit_news_gategory/",data:{pk:e,name:t},success:function(t){200===t.code?window.location.reload():xfzalert.alertErrorToast(t.message)}})}})})},NewsCategory.prototype.run=function(){self=this,self.add_category(),self.delete_category(),self.edit_category()},$(function(){(new NewsCategory).run()});