function NewsList(){}NewsList.prototype.clickcomment=function(){var n=$(".commentBtn"),o=$(".comment-content");n.click(function(){var e=o.val(),t=n.attr("data-news-id");xfzajax.post({url:"/news/news_comment/",data:{content:e,news_id:t},success:function(e){if(200===e.code){var t=e.data,n=template("comment-item",{comment:t});console.log(n),$(".comment-detail").prepend(n),window.messageBox.showSuccess("评论发表成功"),o.val("")}else window.messageBox.showError(e.message)}})})},NewsList.prototype.run=function(){this.clickcomment()},$(function(){(new NewsList).run()});